    fun isPasswordValid (password : String) : Boolean {
        if (password.isEmpty()) {
            return false
        }
        val ptr = Pattern.compile("^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[^a-z0-9]).{8,}$")

        return ptr.matcher(password).matches()
    }

